import { Container } from '@mui/material';
import { styled } from '@mui/system';
import React from 'react';
import banner from "../../assets/aq.jpg";
import './Banner.css';
import { Typography } from '@mui/material';

const StyledBox = styled('div')({
  bannercontent: {
      position: 'relative',
      height: 400,
      display: "flex",
      flexDirection: "column",
      alignItems: "flex-start", // Align items to the start (left)
  },

  tagline: {
      position: 'absolute',
      top: 0,
      left: 0,
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      textAlign: "left",
      fontSize: 60,
      fontWeight: "bold",
      marginBottom: 100,
      fontFamily: "montserrat",
      color: "white", // Set the text color to white or any other color you prefer
      textShadow: "2px 2px 5px rgba(0, 0, 0, 0.5)", // Optional: Add a text shadow for better visibility
      padding: "20px", // Optional: Add some padding for better spacing
  },
});


const Mystyle = styled('div')({
    content: {
        color: "darkgrey",
        textTransform: "capitalize",
        fontFamily: "Montserrat",
    },
});

const Banner = () => {
    return (
        <div>
            <div className='banner'>
                <img src={banner} alt="" />
            </div>
            <Container sx={StyledBox.bannercontent}>
            <Typography variant="h1" className={StyledBox.tagline}>
    Crypto Hunter
</Typography>

                <Typography className={Mystyle.content}>
                    Get all the info regarding your favorite Crypto Currency
                </Typography>
            </Container>
        </div>
    );
}

export default Banner;
