import React, { useEffect, useState } from "react";
import {
    Container,
    createTheme,
    TableCell,
    LinearProgress,
    ThemeProvider,
    Typography,
    TextField,
    TableBody,
    TableHead,
    Table,
    Paper,
    TableContainer,
    Pagination,
} from "@mui/material";
import axios from "axios";
import { CryptoState } from "../CryptoContext";
import { useNavigate } from "react-router-dom";
import { numberWithCommas } from "./Banner/Carousal";
import { useRef } from "react";
import "./CoinsTable.css";
// ... other imports ...
import { TableRow } from "@mui/material";

// ... rest of the code ...


const CoinsTable = () => {
    const [coins, setCoins] = useState([]);
    const [loading, setLoading] = useState(false);
    const [search, setSearch] = useState("");
    const [page, setPage] = useState(1);

    const { currency, symbol } = CryptoState();

    const darkTheme = createTheme({
        palette: {
            primary: {
                main: "#fff",
            },
            type: "light",
        },
    });

    const mounted = useRef(true);

    useEffect(() => {
        return () => {
            mounted.current = false;
        };
    }, []);

    const fetchCoins = async () => {
        try {
            setLoading(true);

            await new Promise(resolve => setTimeout(resolve, 6000));

            const response = await axios.get(
                `https://api.coingecko.com/api/v3/coins/markets?vs_currency=${currency}&order=gecko_desc&per_page=100&page=1&sparkline=false&price_change_percentage=24h`
            );

            if (response.status === 429) {
                console.error("Rate-limiting error. Too many requests.");
                return;
            }

            const data = response.data;
            console.log(data);

            if (!mounted.current) {
                return;
            }

            setCoins(data);
        } catch (error) {
            console.error("Error fetching coins:", error);
        } finally {
            if (!mounted.current) {
                return;
            }

            setLoading(false);
        }
    };

    useEffect(() => {
        fetchCoins();
    }, [currency]);

    const navigate = useNavigate();

    const handleSearch = () => {
        return coins.filter(
            (coin) =>
                coin.name.toLowerCase().includes(search) ||
                coin.symbol.toLowerCase().includes(search)
        );
    };

    return (
        <ThemeProvider theme={darkTheme}>
            <Container className="coins-container">
                <Typography variant="h4" sx={{ margin: 18, fontFamily: "Montserrat" }} className="pb-6">
                    Cryptocurrency Prices by Market Cap
                </Typography>
                <TextField
                    label="Search For a Crypto Currency.."
                    variant="outlined"
                    onChange={(e) => setSearch(e.target.value)}
                />
                <TableContainer component={Paper}>
                    {loading ? (
                        <LinearProgress />
                    ) : (
                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow className="table">
                                    {["Coin", "Price", "24h Change", "Market Cap"].map((head) => (
                                        <TableCell
                                            key={head}
                                            align={head === "Coin" ? "" : "right"}
                                        >
                                            {head}
                                        </TableCell>
                                    ))}
                                </TableRow>
                            </TableHead>

                            <TableBody>
                                {handleSearch()
                                    .slice((page - 1) * 10, (page - 1) * 10 + 10)
                                    .map((row) => {
                                        const profit = row.price_change_percentage_24h > 0;
                                        return (
                                            <TableRow
                                                onClick={() => navigate(`/coins/${row.id}`)}
                                                key={row.name}
                                                className="coin-row"
                                            >
                                                <TableCell
                                                    component="th"
                                                    scope="row"
                                                >
                                                    <img
                                                        src={row?.image}
                                                        alt={row.name}
                                                    />
                                                    <div>
                                                        <span>
                                                            {row.symbol}
                                                        </span>
                                                        <span>{row.name}</span>
                                                    </div>
                                                </TableCell>
                                                <TableCell
                                                    align="right"
                                                    className={
                                                        profit
                                                            ? "green-text"
                                                            : "red-text"
                                                    }
                                                >
                                                    {symbol}
                                                    {numberWithCommas(
                                                        row.current_price.toFixed(2)
                                                    )}
                                                </TableCell>
                                                <TableCell
                                                    align="right"
                                                    className={
                                                        profit
                                                            ? "green-text"
                                                            : "red-text"
                                                    }
                                                >
                                                    {profit && "+"}
                                                    {row.price_change_percentage_24h.toFixed(2)} %
                                                </TableCell>
                                                <TableCell align="right">
                                                    {symbol}{" "}
                                                    {numberWithCommas(
                                                        row.market_cap.toString().slice(0, 6)
                                                    )}
                                                    M
                                                </TableCell>
                                            </TableRow>
                                        );
                                    })}
                            </TableBody>
                        </Table>
                    )}
                </TableContainer>

                <Pagination
                    count={Math.ceil(handleSearch().length / 10)}
                    style={{
                        padding: 20,
                        width: "100%",
                        display: "flex",
                        justifyContent: "center",
                        marginTop: "20px",
                        marginBottom: "20px",
                    }}
                    onChange={(_, value) => {
                        setPage(value);
                        window.scroll(0, 450);
                    }}
                />
            </Container>
        </ThemeProvider>
    );
};

export default CoinsTable;
