import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { styled } from "@mui/system";
import {
    Container,
    createTheme,
    TableCell,
    LinearProgress,
    ThemeProvider,
    Typography,
    TextField,
    TableBody,
    TableRow,
    TableHead,
    Table,
    Paper,
    TableContainer,
    Pagination,
} from "@mui/material";
import axios from "axios";
import { CryptoState } from "../CryptoContext";
import { useNavigate } from "react-router-dom";
import { numberWithCommas } from "./Banner/Carousal";
import "./CoinsTable.css";
import { useRef } from "react";

// Define your styled components
const StyledContainer = styled(Container)({
    textAlign: "center",
    backgroundColor: "fff",
    backgroundSize: "cover",
});

const StyledTableContainer = styled(TableContainer)({
    marginBottom: 20,
});

const StyledTextField = styled(TextField)({
    marginBottom: 100,
    width: "100%",
});

const StyledTableHead = styled(TableHead)({
    backgroundColor: "FFE382",
});

const StyledTableRow = styled(TableRow)({
    backgroundColor: "#16171a",
    cursor: "pointer",
    "&:hover": {
        backgroundColor: "#131111",
    },
    fontFamily: "Montserrat",
});

const MarketUpdate = () => {
    const [data, setData] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [apiLoad, setApiLoad] = useState(true);

    const url = `https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=10&page=${currentPage}&sparkline=false`;

    function numberWithCommas(x) {
        return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
    }

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(url);
                if (!response.ok) {
                    throw new Error("Error!");
                }
                const jsonData = await response.json();
                setData(jsonData);
                setApiLoad(false);
            } catch (error) {
                console.error(error);
            }
        };
        fetchData();
    }, [url]);

    const paginationButtons = [];
    for (let i = 1; i <= 5; i++) {
        paginationButtons.push(
            <button
                key={i}
                onClick={() => setCurrentPage(i)}
                className={i === currentPage ? "activePagi" : ""}
            >
                {i}
            </button>
        );
    }

    const scrollMarket = () => {
        window.scrollTo({
            top: window.pageYOffset - 800,
            behavior: "smooth",
        });
    };

    const scrollTop = () => {
        window.scrollTo({ top: (0, 0), behavior: "smooth" });
    };

    return (
        <>
            <ThemeProvider theme={createTheme({
                palette: {
                    primary: {
                        main: "#fff",
                    },
                    type: "light",
                },
            })}>
                <StyledContainer>
                    <Typography variant="h4" sx={{ margin: 18, fontFamily: "Montserrat" }} className="pb-6">
                        Market Update
                    </Typography>
                    <StyledTableContainer component={Paper}>
                        {apiLoad ? (
                            <LinearProgress sx={{ backgroundColor: "#FFE382" }} />
                        ) : (
                            <Table aria-label="simple table">
                                <StyledTableHead>
                                    <TableRow className="table">
                                        {["Coin", "Price", "24h Change", "Market Cap"].map((head) => (
                                            <TableCell
                                                sx={{
                                                    color: "black",
                                                    fontWeight: "700",
                                                    fontFamily: "Montserrat",
                                                }}
                                                key={head}
                                                align={head === "Coin" ? "" : "right"}
                                            >
                                                {head}
                                            </TableCell>
                                        ))}
                                    </TableRow>
                                </StyledTableHead>

                                <TableBody>
                                    {data.map((item) => (
                                        <Link
                                            onClick={scrollTop}
                                            to={`/coin/${item.id}`}
                                            className="coin-row"
                                            key={item.id}
                                        >
                                            <StyledTableRow>
                                                <TableCell
                                                    component="th"
                                                    scope="row"
                                                    style={{
                                                        display: "flex",
                                                        gap: 15,
                                                    }}
                                                >
                                                    <img
                                                        src={item?.image}
                                                        alt={item.name}
                                                        height="50"
                                                        style={{ marginBottom: 10 }}
                                                    />
                                                    <div style={{ display: "flex", flexDirection: "column", color: "white" }}>
                                                        <span style={{ textTransform: "uppercase", fontSize: 22 }}>
                                                            {item.symbol}
                                                        </span>
                                                        <span style={{ color: "darkgrey" }}>{item.name}</span>
                                                    </div>
                                                </TableCell>
                                                <TableCell
                                                    align="right"
                                                    style={{
                                                        color: item.price_change_percentage_24h >= 0 ? "rgb(14,203,129)" : "red",
                                                        fontWeight: 500,
                                                    }}
                                                >
                                                    {"$"}{""}
                                                    {numberWithCommas(item.current_price.toFixed(2))}
                                                </TableCell>
                                                <TableCell
                                                    align="right"
                                                    style={{
                                                        color: item.price_change_percentage_24h >= 0 ? "rgb(14,203,129)" : "red",
                                                        fontWeight: 500,
                                                    }}
                                                >
                                                    {item.price_change_percentage_24h >= 0 && "+"}
                                                    {item.price_change_percentage_24h.toFixed(2)} %
                                                </TableCell>
                                                <TableCell align="right" style={{ color: "white" }}>
                                                    {"$"} {""} {numberWithCommas(item.market_cap.toString())}
                                                </TableCell>
                                            </StyledTableRow>
                                        </Link>
                                    ))}
                                </TableBody>
                            </Table>
                        )}
                    </StyledTableContainer>

                    <div
                        onClick={scrollMarket}
                        className="market-content__coin-list__pagination"
                    >
                        {paginationButtons}
                    </div>

                </StyledContainer>
            </ThemeProvider>
        </>
    );
};

export default MarketUpdate;
