import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { CryptoState } from '../CryptoContext';
import axios from 'axios';
import { SingleCoin } from '../config/api';
import { Typography, styled } from '@mui/material';
import CoinInfo from '../components/CoinInfo';

export function numberWithCommas(x) {
  return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

const Coinpage = () => {
  const { id } = useParams();
  const [coin, setCoin] = useState();
  const { currency, symbol } = CryptoState();

  const fetchCoin = async () => {
    try {
      const { data } = await axios.get(SingleCoin(id));
      setCoin(data);
    } catch (error) {
      console.error("Error fetching coin data:", error);
    }
  };

  useEffect(() => {
    fetchCoin();
  }, []);

  const Container = styled('div')(({ theme }) => ({
    backgroundColor: "#14161a",
    display: 'flex',
    flexDirection: 'column', // Ensure it's always a column
    alignItems: 'center',
  }));

  const Sidebar = styled('div')(({ theme }) => ({
    width: '30%',
    [theme.breakpoints.down('md')]: {
      width: '100%',
    },
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: 25,
    borderRight: '2px solid grey',
  }));

  const HeadingText = styled(Typography)({
    fontWeight: 'bold',
    marginBottom: 20,
    fontFamily: 'Montserrat',
  });

  const DescriptionText = styled(Typography)({
    width: "100%",
    fontFamily: "Montserrat",
    padding: 25,
    paddingBottom: 15,
    paddingTop: 0,
    textAlign: "justify",
  });

  const MarketDataText = () => {
    const { breakpoints } = useTheme();

    return (
      <Typography
        sx={{
          alignSelf: "start",
          padding: "10px", // Adjust padding to your preference
          width: "100%",
          maxWidth: "1000px",
          margin: "0 auto", // Center the element

          [breakpoints.down("md")]: {
            display: "flex",
            justifyContent: "space-around",
          },
          [breakpoints.down("sm")]: {
            flexDirection: "column",
            alignItems: "center",
          },
          [breakpoints.down("xs")]: {
            alignItems: "start",
          },
        }}
      >
        {/* Your content goes here */}
      </Typography>
    );
  };



  return (
    <Container>
      <Sidebar>
        {coin && (
          <>
            <img
              src={coin?.image.large}
              alt={coin?.name}
              height="200"
              style={{ marginBottom: 20 }}
            />
            <HeadingText variant="h3">{coin?.name}</HeadingText>
            <DescriptionText
              variant="subtitle1"
              dangerouslySetInnerHTML={{ __html: coin?.description.en.split('. ')[0] }}
            />
            <div  className={MarketDataText}>
              <span style={{ display: "flex" }}>
                <Typography variant='h5' className={HeadingText}>
                  Rank:
                </Typography>
                &nbsp; &nbsp;
                <Typography
                  variant='h5'
                  style={{
                    fontFamily: "Montserrat",
                  }}
                >
                  {coin?.market_cap_rank}
                </Typography>
              </span>
              <span style={{ display: "flex" }}>
                <Typography variant='h5' className={HeadingText}>
                  Current Price
                </Typography>
                &nbsp; &nbsp;
                <Typography
                  variant='h5'
                  style={{
                    fontFamily: "Montserrat",
                  }}
                >
                  {symbol}{""}
                  {coin?.market_data?.current_price?.[currency.toLowerCase()] &&
                    numberWithCommas(coin.market_data.current_price[currency.toLowerCase()])}
                </Typography>
              </span>
              <span style={{ display: "flex" }}>
                <Typography variant='h5' className={HeadingText}>
                  Market Cap
                </Typography>
                &nbsp; &nbsp;
                <Typography
                  variant='h5'
                  style={{
                    fontFamily: "Montserrat",
                  }}
                >
                  {symbol}{" "}
                  {coin?.market_data?.market_cap?.[currency.toLowerCase()] &&
                    numberWithCommas(
                      coin.market_data.market_cap[currency.toLowerCase()]
                        .toString()
                        .slice(0, -6)
                    )} M
                </Typography>
              </span>
            </div>
          </>
        )}
      </Sidebar>
      <CoinInfo coin={coin} />
    </Container>
  );
};

export default Coinpage;
