import React, { useEffect, useState } from "react";
import { LinearProgress, Typography } from "@material-ui/core";
import axios from "axios";
import { useParams } from "react-router-dom";
import CoinInfo from "../components/CoinInfo";
import { SingleCoin } from "../config/api";
import { numberWithCommas } from "../components/CoinsTable";
import { CryptoState } from "../CryptoContext";

const CoinPage = () => {
  const { id } = useParams();
  const [coin, setCoin] = useState();

  const { currency, symbol } = CryptoState();

  const fetchCoin = async () => {
    const { data } = await axios.get(SingleCoin(id));
    setCoin(data);
  };

  useEffect(() => {
    fetchCoin();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  if (!coin) return <LinearProgress style={{ backgroundColor: "gold" }} />;

  return (
    <div>
      <img
        src={coin?.image.large}
        alt={coin?.name}
        height="200"
        style={{ marginBottom: 20 }}
      />
      <Typography variant="h3" style={{ fontWeight: "bold", marginBottom: 20 }}>
        {coin?.name}
      </Typography>
      <Typography variant="subtitle1" style={{ width: "100%", padding: 25, paddingBottom: 15, paddingTop: 0, textAlign: "justify" }}>
        {coin?.description.en.split(". ")[0]}.
      </Typography>
      <div style={{ alignSelf: "start", padding: 25, paddingTop: 10, width: "100%" }}>
        <span style={{ display: "flex" }}>
          <Typography variant="h5" style={{ fontWeight: "bold", marginBottom: 20 }}>
            Rank:
          </Typography>
          &nbsp; &nbsp;
          <Typography variant="h5">
            {numberWithCommas(coin?.market_cap_rank)}
          </Typography>
        </span>
        <span style={{ display: "flex" }}>
          <Typography variant="h5" style={{ fontWeight: "bold", marginBottom: 20 }}>
            Current Price:
          </Typography>
          &nbsp; &nbsp;
          <Typography variant="h5">
            {symbol}{" "}
            {numberWithCommas(
              coin?.market_data.current_price[currency.toLowerCase()]
            )}
          </Typography>
        </span>
        <span style={{ display: "flex" }}>
          <Typography variant="h5" style={{ fontWeight: "bold", marginBottom: 20 }}>
            Market Cap:
          </Typography>
          &nbsp; &nbsp;
          <Typography variant="h5">
            {symbol}{" "}
            {numberWithCommas(
              coin?.market_data.market_cap[currency.toLowerCase()]
                .toString()
                .slice(0, -6)
            )}
            M
          </Typography>
        </span>
      </div>
      <CoinInfo coin={coin} />
    </div>
  );
};

export default CoinPage;
