import { Container, Typography } from "@mui/material";
import { styled } from "@mui/system";
import banner from "../../assets/aq.jpg"
import Carousal from "./Carousal";
import btc from "../../assets/biticon.png"
import "./Banner.css"
import btc2 from "../../assets/Free_btc-remove.png"

const StyledBanner = styled("div")({
  backgroundImage: `url(${banner})`,
  height: "90vh",
  backgroundSize: "cover",
  backgroundPosition: "center",
  backgroundColor: "black",
});

const StyledBannerContent = styled(Container)({
  height: "100%",
  display: "flex",
  flexDirection: "column",
  paddingTop: 25,
  justifyContent: "space-around",
});

const StyledTagline = styled("div")({
  display: "flex",
  height: "100%",
  flexDirection: "column",
  justifyContent: "center",
  textAlign: "center",
});

const Banner = () => {
  return (
    <div>
      <StyledBanner>
        <StyledBannerContent>

          <StyledTagline>
            <Typography
              variant="h2"
              sx={{
                fontWeight: "bold",
                marginBottom: 15,
                fontFamily: "Montserrat",
                paddingTop: 20,
                textAlign: "center",
              }}
            >
                        <img src={btc2}  style={{ height: "150px"
           ,
            width : "200px" ,
              }}></img>
              Crypto Mars <img src={btc}  style={{
                alignItems: "center",
              }} />
            </Typography>
            <Typography
              variant="subtitle2"
              sx={{
                color: "darkgrey",
                textTransform: "capitalize",
                fontFamily: "Montserrat",
                paddingBottom: 10,
              }}
            >
              Get all the Info regarding your favorite Crypto Currency
            </Typography>
          </StyledTagline>
          <Carousal />

        </StyledBannerContent>


      </StyledBanner>
    </div>
  );
};

export default Banner;
