import { Container, Typography, Grid } from '@mui/material';
import { styled } from '@mui/system';
import React from 'react';
import banner from "../../assets/aq.jpg";
import './Banner.css';

const StyledBox = styled('div')({
    bannercontent: {
        height: '100%', // Set to 100% height
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        position: "relative",
    },

    image: {
        width: '100%', // Ensure the image takes up the entire width of the container
        height: '100%', // Ensure the image takes up the entire height of the container
        objectFit: 'cover', // Maintain aspect ratio and cover the container
        position: 'absolute',
        top: 0,
        left: 0,
        zIndex: -1, // Move the image to the background
    },

    tagline: {
        backgroundColor: "rgba(0, 0, 0, 0.7)",
        padding: "20px",
        borderRadius: "10px",
        zIndex: 1, // Place the text above the image
    },
});

const Mystyle = styled('div')({
    content: {
        color: "darkgrey",
        textTransform: "capitalize",
        fontFamily: "Montserrat",
    },
});

const Banner = () => {
    return (
        <div>
            <Container sx={StyledBox.bannercontent}>
                <img src={banner} alt="Banner" className={StyledBox.image} />
                <Grid container>
                    <Grid item xs={12} md={6}>
                        <div className={StyledBox.tagline}>
                            <Typography variant="h1" sx={{ color: "white" }}>
                                Crypto Hunter
                            </Typography>
                            <Typography className={Mystyle.content}>
                                Get all the info regarding your favorite Crypto Currency
                            </Typography>
                        </div>
                    </Grid>
                </Grid>
            </Container>
        </div>
    );
}

export default Banner;
