import { Container } from '@mui/system';
import React from 'react';
import banner from "../../assets/aq.jpg";

const Banner = () => {
  return (
    <div
      sx={{
        backgroundImage: `url(${banner})`, // Use the imported image variable
         // Adjust the background size as needed
      }}
    >
      <Container
        sx={{
          height: 400,
          display: "flex",
          flexDirection: "column",
          paddingTop: 25,
          justifyContent: "space-around",
          backgroundColor: 'rgba(0, 0, 0, 0.5)', // Add a semi-transparent overlay if needed
        }}
      >
        {/* You can include other content or components here */}
        <img src={banner} alt="Banner" style={{ width: '100%', height: 'auto' }} />
      </Container>
    </div>
  );
}

export default Banner;
