    ))
}

const CoinsTable = () => {

    const [coins,setCoins]  = useState();
    const [loading,setLoading] = useState(false);
    const [serach,setSearch] = useState("")
    const {currency} = CryptoState();

    const fecthcoins = async () => {
        setLoading(true)
        const {data} =  await axios.get(CoinList(currency));

        setCoins(data);
        setLoading(false);
    };
    console.log(coins); 
    useEffect(() => {

        fecthcoins()

    }, [currency])


    return (

    <ThemeProvider theme={darkTheme}>
        <Container variant="h4" style={{ margin:18, textAlign: "center" }}>
        <Typography>
            Cryptocurrency Prices by Market Cap
        </Typography>
        <TextField label="Search For a crypto Currency" variant='outlined' style={{ marginBottom:20 , width: "100%" }} onChange={(e) => setSearch(e.target.value)}/>
        <TableContainer>
            {loading ? (
                <LinearProgress style={{ backgroundColor:"gold" }} />
            ) : (
                <Table>
                    <TableHead style={{ backgroundColor: "#EEBC1D" }}>
                        <TableRow>
                        {["coin","Price","24hr Change", "Market Cap"].map((head) => (
                            <TableCell style={{
                                color : "black",
                                fontWeight: "700",
                                fontFamily : "Montserrat"
                            }}
                            key={head}
                            align={head === "coin" ? "" :"right"}
                            >
                            {head}    
                            </TableCell>
                        ))};
                        </TableRow>
                    </TableHead>
                    <TableBody>
                                {handleSearch}
                    </TableBody>
                </Table>
            )
         };

        </TableContainer>
        </Container>
    </ThemeProvider>    
  )
}

export default CoinsTable;
