import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { CryptoState } from '../CryptoContext';
import axios from 'axios';
import { SingleCoin } from '../config/api';
import { Typography, styled, useTheme } from '@mui/system'; // Use useTheme from @mui/system
import CoinInfo from '../components/CoinInfo';

const Coinpage = () => {
  const { id } = useParams();
  const [coin, setCoin] = useState();
  const { currency, symbol } = CryptoState();
  const theme = useTheme(); // Use useTheme

  const fetchCoin = async () => {
    const { data } = await axios.get(SingleCoin(id));
    setCoin(data);
  };

  useEffect(() => {
    fetchCoin();
  }, []);

  // Use the styled function correctly
  const Sidebar = styled('div')({
    width: '30%',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '25px',
    borderRight: '2px solid grey',
  });

  const Heading = styled(Typography)({
    fontWeight: 'bold',
    marginBottom: '20px',
    fontFamily: 'Montserrat',
  });

  const Description = styled(Typography)({
    width: '100%',
    fontFamily: 'Montserrat',
    padding: '25px',
    paddingBottom: '15px',
    paddingTop: '0',
    textAlign: 'justify',
  });

  const MarketData = styled(Typography)({
    alignSelf: 'start',
    padding: '25px',
    paddingTop: '10px',
    width: '100%',
    [theme.breakpoints.down('md')]: {
      display: 'flex',
      justifyContent: 'space-around',
    },
    [theme.breakpoints.down('sm')]: {
      flexDirection: 'column',
      alignItems: 'center',
    },
    [theme.breakpoints.down('xs')]: {
      alignItems: 'start',
    },
  });

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <Sidebar>
        <img src={coin?.image.large} alt={coin?.name} height="200" style={{ marginBottom: '20px' }} />
        <Heading variant="h3">{coin?.name}</Heading>
        <Description
          variant="subtitle1"
          dangerouslySetInnerHTML={{ __html: coin?.description.en.split('. ')[0] }}
        />

        <div className={MarketData}>
          <span style={{ display: 'flex' }}>
            <Typography variant="h5" className={Heading}>
              Rank:
            </Typography>
            &nbsp; &nbsp;
            <Typography variant="h5" style={{ fontFamily: 'Montserrat' }}>
              {coin?.market_cap_rank}
            </Typography>
          </span>
        </div>
      </Sidebar>
      <CoinInfo coin={coin} />
    </div>
  );
};

export default Coinpage;
