import axios from 'axios';
import React, { useState ,useEffect } from 'react'
import { CoinList  } from '../config/api';
import { CryptoState } from '../CryptoContext';
import { ThemeProvider } from 'styled-components';
import { Container } from '@mui/system';
import { TextField, Typography,createTheme } from '@mui/material';


const darkTheme = createTheme({
    palette: {
        primary: {
            main: "#fff",
        },
        type: "dark",
    },
});


const CoinsTable = () => {

    const [coins,setCoins]  = useState();
    const [loading,setLoading] = useState(false);
    const {currency} = CryptoState();

    const fecthcoins = async () => {
        setLoading(true)
        const {data} =  await axios.get(CoinList(currency));

        setCoins(data);
        setLoading(false);
    };
    console.log(coins); 
    useEffect(() => {

        fecthcoins()

    }, [currency])


    return (

    <ThemeProvider theme={darkTheme}>
        <Container variant="h4" style={{ margin:18, textAlign: "center" }}>
        <Typography>
            Cryptocurrency Prices by Market Cap
        </Typography>
        <TextField label="Search For a crypto Currency" variant='outlined' style={{ marginBottom:20 , width: "100%" }}>

        </TextField>
        </Container>
    </ThemeProvider>    
  )
}

export default CoinsTable;
